import React from 'react';
import { useNavigate } from 'react-router-dom';

export default function Home() {
  const navigate = useNavigate();

  const handleRedirect = () => {
    navigate('/Products'); // Redirect to the orders page
  };

  const menuItems = [
    { id: 1, name: 'Burger', price: 5.99, image: 'https://www.shutterstock.com/image-photo/classic-hamburger-stock-photo-isolated-600nw-2282033179.jpg' },
    { id: 2, name: 'Pizza', price: 8.99, image: 'https://c4.wallpaperflare.com/wallpaper/234/543/684/food-pizza-wallpaper-preview.jpg' },
    { id: 3, name: 'Pasta', price: 7.99, image: 'https://theplantbasedschool.com/wp-content/uploads/2024/01/Penne-Arrabbiata-25.jpg' },
    { id: 4, name: 'Coke', price: 1.99, image: 'https://m.media-amazon.com/images/I/91yrYlUSnJL.jpg' },
    { id: 5, name: 'Chicken Wings', price: 2.99, image: 'https://t3.ftcdn.net/jpg/02/91/35/16/360_F_291351654_FFAS60r2iHUkOY69RPRwEOVS76EU4SdA.jpg' },
    { id: 6, name: 'Burritos', price: 4.99, image: 'https://i1.wp.com/www.splashoftaste.com/wp-content/uploads/2023/04/Veggie-Burritos-featured.jpg' },
    { id: 7, name: 'Ice Cream', price: 3.49, image: 'https://media-assets.swiggy.com/swiggy/image/upload/fl_lossy,f_auto,q_auto,w_366/5db28ee7aed5c06a08111207a24c6a2c' },
    { id: 8, name: 'Sandwich', price: 6.49, image: 'https://media.istockphoto.com/id/1127940785/photo/stacked-vegetarian-sandwiches-of-arugula-artichoke-sun-dried-tomato-pesto.jpg?s=612x612&w=0&k=20&c=m0NkVUEbI_Q4sBcVc270-DWzaZ-lSKottYyqS-smqIA=' },
    { id: 9, name: 'Chicken Nuggets', price: 2.49, image: 'https://img.freepik.com/premium-photo/gourmet-chicken-nuggets-with-bbq-sauce-chicken-nugget-fried-food-image-photography_1020697-131153.jpg' },
    { id: 10, name: 'Fries', price: 2.99, image: 'https://img.freepik.com/premium-photo/portion-french-fries_908985-48851.jpg' },
  ];

  return (
    <div className='container-fluid p-5'>
      <h2 
        onClick={handleRedirect} 
        style={{ cursor: 'pointer', color: 'black' }} 
      >
        {/* Go to Orders Section. */}
      </h2>
      <h3 className='mt-4'>Food and Beverages Menu</h3>
      <div className='row'>
        {menuItems.map(item => (
          <div className='col-md-4 mb-3' key={item.id}>
            <div className='card'>
              <img src={item.image} alt={item.name} className='card-img-top' />
              <div className='card-body'>
                <h5 className='card-title'>{item.name}</h5>
                <p className='card-text'>Price: ₹{item.price.toFixed(2)}</p>
                <button 
                  className='btn btn-primary' 
                  onClick={handleRedirect}
                >
                  Order
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
