import React from 'react';
import { useNavigate } from 'react-router-dom';

export default function Home() {
  const navigate = useNavigate();

  const handleRedirect = () => {
    navigate('/Products'); // Redirect to the orders page
  };

  const menuItems = [
    { id: 1, name: 'Burger', price: 5.99, image: 'https://unsplash.com/photos/burger-with-lettuce-and-tomatoes-sc5sTPMrVfk' },
    { id: 2, name: 'Pizza', price: 8.99, image: 'https://example.com/pizza.jpg' },
    { id: 3, name: 'Pasta', price: 7.99, image: 'https://example.com/pasta.jpg' },
    { id: 4, name: 'Soda', price: 1.99, image: 'https://example.com/soda.jpg' },
    { id: 5, name: 'Coffee', price: 2.99, image: 'https://example.com/coffee.jpg' },
    { id: 6, name: 'Salad', price: 4.99, image: 'https://example.com/salad.jpg' },
    { id: 7, name: 'Ice Cream', price: 3.49, image: 'https://example.com/icecream.jpg' },
    { id: 8, name: 'Sandwich', price: 6.49, image: 'https://example.com/sandwich.jpg' },
    { id: 9, name: 'Juice', price: 2.49, image: 'https://example.com/juice.jpg' },
    { id: 10, name: 'Fries', price: 2.99, image: 'https://example.com/fries.jpg' },
  ];

  return (
    <div className='container-fluid p-5'>
      <h2 
        onClick={handleRedirect} 
        style={{ cursor: 'pointer', color: 'black' }} 
      >
        {/* Go to Orders Section. */}
      </h2>
      <h3 className='mt-4'>Food and Beverages Menu</h3>
      <div className='row'>
        {menuItems.map(item => (
          <div className='col-md-4 mb-3' key={item.id}>
            <div className='card'>
              <img src={item.image} alt={item.name} className='card-img-top' />
              <div className='card-body'>
                <h5 className='card-title'>{item.name}</h5>
                <p className='card-text'>Price: â‚¹{item.price.toFixed(2)}</p>
                <button 
                  className='btn btn-primary' 
                  onClick={handleRedirect}
                >
                  Order
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
